package javachallenge;

public class AddTwoDigits {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a =33;
		int b=a/10;
		
		System.out.println(" b value " +b);
		
		int c=a%10;
		System.out.println(" c value " +c);
		int sum=b+c;
		System.out.println(sum);
	}

}
